import { Fragment } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import {
  Box,
  Grid,
  Text,
  Button,
  Flex,
  Input,
  FormLabel,
  Textarea,
  Center
} from "@chakra-ui/react";

const validationSchema = Yup.object().shape({
  firstName: Yup.string().required("First Name is required"),
  lastName: Yup.string().required("Last Name is required"),
  job: Yup.string()
    .job("Invalid job address")
    .required("Email is required"),
  phoneNumber: Yup.string()
    .matches(/^[0-9]+$/, "Phone Number must contain only digits")
    .min(10, "Phone Number must be at least 10 digits")
    .max(15, "Phone Number can't exceed 15 digits")
    .required("Phone Number is required"),
  subject: Yup.string().required("Subject is required"),
  bio: Yup.string().required("Message is required")
});

const ContactUs = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      dateOfBirth: "",
      job: "",
      bio: "",
      location: {
        city: '',
        country: '',
        long: '',
        lat: '',
      },
    },
    validationSchema: validationSchema,
    onSubmit: (values, { setSubmitting }) => {
      if (
        !values.firstName ||
        !values.lastName ||
        !values.dateOfBirth||
        !values.job ||
        !values.bio ||
        !values.location
      ) {
        alert("Please fill in all required fields");
      } else {
        console.log(values);
      }
      setSubmitting(false);
    }
  });

  return (
    <Fragment>
      <Box mt="40px">
        <Box
          backgroundColor="rgba(1, 190, 110, 0.34)"
          p={{ base: "20px", md: " 100px" }}
          borderRadius={{ base: "5px", md: "15px" }}
          id="contactUs"
        >
          <Box
            backgroundColor="#fff"
            borderRadius={{ base: "5px", md: "15px" }}
          >
            <Box padding={2}>
              <Box mt="40px">
                <Text
                  color="#000"
                  textAlign="center"
                  fontSize={{ base: "24px", md: "40px" }}
                  fontWeight={700}
                >
                  CONTACT US
                </Text>
              </Box>
              <Center mt="10px">
                <Text
                  color="#000"
                  textAlign={{ base: "left", md: "center" }}
                  fontSize={{ base: "16px", md: "36px" }}
                  fontWeight={500}
                >
                  We'd would love to hear from you
                </Text>
              </Center>
            </Box>

            <Box p={{ base: "20px", md: "20px 100px 50px " }}>
              <form onSubmit={formik.handleSubmit}>
                <Grid
                  gridTemplateColumns={{
                    base: "repeat(4)",
                    md: "repeat(2, 2fr)"
                  }}
                  gap={4}
                >
                  <Box gap={2}>
                    <FormLabel>First Name</FormLabel>
                    <Input
                      id="firstName"
                      name="firstName"
                      type="text"
                      placeholder="First Name"
                      size={{ base: "md", md: "lg" }}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.firstName}
                      focusBorderColor="rgba(1, 190, 110, 0.34)"
                    />
                    {formik.touched.firstName && formik.errors.firstName && (
                      <Text color="#FF0000" fontSize="14px" mt="2px">
                        {formik.errors.firstName}
                      </Text>
                    )}
                  </Box>

                  <Box>
                    <FormLabel>Last Name</FormLabel>
                    <Input
                      id="lastName"
                      name="lastName"
                      type="text"
                      placeholder="Last Name"
                      size={{ base: "md", md: "lg" }}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.lastName}
                      focusBorderColor="rgba(1, 190, 110, 0.34)"
                    />
                    {formik.touched.lastName && formik.errors.lastName && (
                      <Text color="#FF0000" fontSize="14px" mt="2px">
                        {formik.errors.lastName}
                      </Text>
                    )}
                  </Box>

                  <Box>
                    <FormLabel>Email</FormLabel>
                    <Input
                      id="job"
                      name="job"
                      type="job"
                      placeholder="what is your job"
                      size={{ base: "md", md: "lg" }}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.job}
                      focusBorderColor="rgba(1, 190, 110, 0.34)"
                    />
                    {formik.touched.job && formik.errors.job && (
                      <Text color="#FF0000" fontSize="14px" mt="2px">
                        {formik.errors.job}
                      </Text>
                    )}
                  </Box>

                  <Box>
                    <FormLabel>Phone Number</FormLabel>
                    <Input
                      id="phoneNumber"
                      name="phoneNumber"
                      type="tel"
                      placeholder="Phone Number"
                      size={{ base: "md", md: "lg" }}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.phoneNumber}
                      focusBorderColor="rgba(1, 190, 110, 0.34)"
                    />
                    {formik.touched.phoneNumber &&
                      formik.errors.phoneNumber && (
                        <Text color="#FF0000" fontSize="14px" mt="2px">
                          {formik.errors.phoneNumber}
                        </Text>
                      )}
                  </Box>
                </Grid>

                <FormLabel mt="20px">Subject</FormLabel>
                <Input
                  id="subject"
                  name="subject"
                  type="text"
                  placeholder="Subject"
                  size={{ base: "md", md: "lg" }}
                  onChange={formik.handleChange}
                  onBlur={formik.handleBlur}
                  value={formik.values.phoneNumber}
                  focusBorderColor="rgba(1, 190, 110, 0.34)"
                />
                {formik.touched.subject && formik.errors.subject && (
                  <Text color="#FF0000" fontSize="14px" mt="2px">
                    {formik.errors.subject}
                  </Text>
                )}

                <FormLabel mt="20px">Message</FormLabel>
                <Textarea
                  id="bio"
                  name="bio"
                  placeholder="Type a Message"
                  rows={6}
                  size={{ base: "md", md: "lg" }}
                  onChange={formik.handleChange}
                  onBlur={formik.handleBlur}
                  value={formik.values.bio}
                  focusBorderColor="rgba(1, 190, 110, 0.34)"
                />
                {formik.touched.bio && formik.errors.bio && (
                  <Text color="#FF0000" fontSize="14px" mt="2px">
                    {formik.errors.bio}
                  </Text>
                )}
                <Button
                  type="submit"
                  style={{
                    backgroundColor: !formik.isValid ? "#94f2ca" : "#01BE6E",
                    color: "#fff",
                    fontSize: "16px",
                    borderRadius: "15px",
                    padding: "10px 24px",
                    height: "60px",
                    marginTop: "20px",
                    width: "100%"
                  }}
                  disabled={!formik.isValid}
                >
                  Submit
                </Button>
              </form>
